import React, {Component} from "react";
import {Icon} from "@iconify/react";
import firebaseIcon from "@iconify/icons-logos/firebase";
import reactIcon from "@iconify/icons-logos/react";
import pythonIcon from "@iconify/icons-logos/python";
import awsIcon from "@iconify/icons-logos/aws";
import promo from "../media/promo.png";

class About extends Component {
  render() {
    if (this.props.sharedBasicInfo) {
      var profilepic = "images/" + this.props.sharedBasicInfo.image;
    }
    if (this.props.resumeBasicInfo) {
      var sectionName = this.props.resumeBasicInfo.section_name.overview;
      var hello = this.props.resumeBasicInfo.overview_header;
      var about = this.props.resumeBasicInfo.description;
    }

    return (
      <section id='overview'>
        <div className='card'>
          <div
            className='card-body font-trebuchet text-justify ml-3 mr-3'
            style={{
              height: "auto",
              fontSize: "132%",
              lineHeight: "200%",
            }}>
            <br />
            <span className='wave'>Dawn: Anonymous Dating App</span>
            <br />
            <br />
            <ul>
              <li>
                Facilitated More than 5000 connections between over 1300
                registered users
              </li>
              <li>
                Engaged around 1000 users for an average of 15 minutes and 52
                seconds on a daily basis
              </li>
              <li>
                Designed, programed and deployed the entire application with
                React Native and Google Firebase
              </li>
              <li>
                Designed, programed and deployed the entire application with
                React Native and Google Firebase
              </li>
              <li>
                Developed a recommendation algorithim to match users according
                to their preferences
              </li>
              <li>
                Devoloped profile edit features that allowed users to add
                images, GIFs, stickers and text to their profile
              </li>
              <li>
                Devoloped reveal feature to allow users to reveal their identity
                if they both agree to do so
              </li>
              <li>
                Analyzed user Analytics with Mixpanel, used the data to improve
                the experiance
              </li>
            </ul>
          </div>
        </div>
      </section>
    );
  }
}

export default About;
